import React from "react";
import SignUpButton from './SignUpButton.js';

const BannerFold = () => {
    return (
        <div className = "bannerFold">
            <h1>Sign up for <br/> your first week <br/> FREE!</h1>
            <SignUpButton/>
            <style jsx> {`
                .bannerFold {
                    background-image: url("https://makeovergymfitness.s3-us-west-1.amazonaws.com/index_main_image.jpg");
                    background-size: cover;
                    height: 70vh;
                    max-width: 100%;
                }

                .bannerFold h1 {
                    margin: 0;
                    color: white;
                    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
                    font-size: 5vw;
                    font-family: Helvetica;
                    font-weight: 900;
                    text-align: left;
                    padding-left: 100px;
                    padding-bottom: 0px;
                }

                @media screen and (max-width: 820px) {
                    .bannerFold {
                        background-image: url("https://makeovergymfitness.s3-us-west-1.amazonaws.com/index_secondary_image.jpeg");
                        background-color: black;
                        text-align: left;
                    }

                    .bannerFold h1 {
                        font-size: 10vw;
                        padding-left: 10px;
                        padding-top: 10px;
                        text-align: left;
                    }
                }
            `}</style>
        </div>
    );
}

export default BannerFold;
